import Home from '@/views/Home.vue';
import About from '@/views/About.vue';
import Import from '@/views/Import.vue';
import Display from '@/views/Display.vue';
import Analysis from '@/views/Analysis.vue';
import Login from '@/views/Login.vue';
import { createRouter, createWebHistory } from 'vue-router';

const routes = [
    {
        path: '/',
        name: 'Home',
        component: Home,
    },
    {
        path: '/about',
        name: 'About',
        component: About,
    },
    {
        path: '/import',
        name: 'Import',
        component: Import,
    },
    {
        path: '/display',
        name: 'Display',
        component: Display,
    },
    {
        path: '/analysis',
        name: 'Analysis',
        component: Analysis,
    },
    {
        path: '/login',
        name: 'Login',
        component: Login,
    },
];


const scrollBehavior = function (to, from, savedPosition) {
    if (savedPosition) {
      return savedPosition;
    } else {
      let position = {};
      if (to.hash) {
        position = { selector: to.hash };
      } else {
        // 获取目标页面的第一个元素
        const targetElement = document.querySelector(to.matched[0].selector);
        if (targetElement) {
          // 滚动到目标元素的顶部
          position = { top: targetElement.offsetTop };
        }
      }
      return position;
    }
  };




const router = createRouter({
    history: createWebHistory(process.env.BASE_URL),
    routes,
    scrollBehavior,
});

export default router;
