export default {
  name: 'TransitionRouterView',
  data() {
    return {
      transitionName: ''
    };
  },
  beforeRouteEnter(to, from, next) {
    const fromName = from.name;
    const toName = to.name;
    let transitionName = '';
    if (toName === 'Home' && fromName === 'About') {
      transitionName = 'slide-down';
    } else if (toName === 'About' && fromName === 'Home') {
      transitionName = 'slide-up';
    } else if (['Import', 'Display', 'Analysis'].includes(toName) && ['Home', 'About'].includes(fromName)) {
      transitionName = 'slide-left';
    } else if (['Home', 'About'].includes(toName) && ['Import', 'Display', 'Analysis'].includes(fromName)) {
      transitionName = 'slide-right';
    }
    next(vm => {
      vm.transitionName = transitionName;
    });
  }
};
